-- PostgreSQL schema for Grant Database
-- Compatible with render.com

DROP TABLE IF EXISTS questions CASCADE;

CREATE TABLE questions (
    id SERIAL PRIMARY KEY,
    question_number VARCHAR(20) UNIQUE NOT NULL,
    group_number VARCHAR(10) NOT NULL,
    question_text TEXT NOT NULL,
    russian TEXT,
    danish TEXT,
    muira TEXT,
    nganasan TEXT,
    polish TEXT,
    circassian TEXT
);

CREATE INDEX idx_group_number ON questions(group_number);

-- Insert data
INSERT INTO questions (question_number, group_number, question_text, russian, danish, muira, nganasan, polish, circassian) VALUES (
    '1.1',
    '1',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет)',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет) •	Дa ― для всех финитных клауз, вводимых подчинительным союзом и вопросительно-относительным словом.',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет) •	Да. В зависимой и главной клаузе разный порядок слов.',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет) Нет, универсальной диагностики определения границ клауз выявлено не было',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет) •	Да — например, для адвербиальных клауз',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы (да / нет) •	Да',
    'Доступна ли для изучаемого языка не зависящая от выбора формальной модели языка диагностика определения границ зависимой и главной клаузы? Универсальной диагностики выявлено не было. Принадлежность главной или зависимой клаузе можно однозначно определить для аргументных именных групп, маркированных падежом, в случаях, когда матричный и зависимый предикаты приписывают выражаю- щей соотв. аргумент именной группе разные падежи [Тестелец 2009; Ershova 2019]. (1)	a.	pŝaŝe-r	faj	[∅	wered	q-ə-ʔʷe-n-ew]. девочка-ABS хотеть		песня	DIR-3SG.ERG-петь-ADV ‘Девочка хочет спеть песню.’ [Тестелец 2009: 655] b.	[pŝaŝe-m	wered	q-ə-ʔʷe-n-ew]	∅	faj. девочка-ERG песня	DIR-3SG.ERG-петь-ADV		хотеть ‘Девочка хочет спеть песню.’ [Тестелец 2009: 655]'
);

INSERT INTO questions (question_number, group_number, question_text, russian, danish, muira, nganasan, polish, circassian) VALUES (
    '1.2',
    '1',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет)',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет) •	Да. В предложениях с глаголами речевой каузации и вложенной инфинитивной группы аргумент в дат.п. в предложениях типа Учитель велел трем мальчикам из класса / кому-нибудь остаться может включаться либо в состав зависимой клаузы [Minor 2013] ― гипотеза о скрытом рестрикторе (hidden restrictor analysis), либо в состав главной клаузы [Burukina 2019; 2020] ― гипотеза подъема аргумента. •	Да ― для случаев, где выделение зависимой клаузы зависит от принятых критериев выделения малых клауз. Ср. предложения с адвербиальной группой при глаголе подъема выглядеть ‘Х выглядит как Y’ типа Задача выглядит просто. При допущении, что аргументная малая клауза может быть выражена в русском языке адвербиальной группой, наречие  просто в структуре Задачаi выглядела [SC-ARG ___i [AdvP просто]] трактуется как вторичный предикат [Zimmerling 2025]. В противном случае оно описывается, как непредикативный модификатор глагола, и зависимая клауза не выделяется. •	Да ― для синтаксических идиом с отрицательными предикативными словами, ср. (мне) негде спать, (мне) некого винить. При декомпозиционном анализе таких идиом как нестандартных полипредикатных структур, возглавляемых отрицательным экзистенциальным предикатом [Rappaport 1986; Апресян, Иомдин 1989/2010; Babby 2000; Циммерлинг 2022; Мельчук 2025], вопросительно-относительное местоимение считается элементом нижней клаузы и зависимым вложенного инфинитива: °спать  где, °винить  кого. Аргумент в дат.п. в структурах данного типа может относиться к зависимой клаузе, если анализ с подъемом не принимается [Апресян, Иомдин 1989/2010], либо к главной клаузе, если принимается гипотеза подъема [Babby 2000] или аттракции аргумента  [Mel’čuk 2025]. Обе возможности допускаются для разных типов синтаксических идиом в [Zimmerling 2024].',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет) •	Да. В некоторых случаях наличие зависимой инфинитивной и причастной клаузы не признается традиционными грамматиками датского языка.',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет) Да, в (1) имеет место дистантное согласование с абсолютивной ИГ зависимой клаузы. В подобных случаях можно предположить, что ИГ, являющаяся контролером согласования, находится либо на периферии зависимой клаузы (1а), либо передвигается в позицию объекта главной клаузы (1)	a.  Передвижение ИГ зависимой клаузы на левую периферию ʡaˤli-s Ali-dat saj Cop.m ršwal five čutːui chudu.aBs pat’imat-li Patimat-Erg ti d-arq’-ib-lis npL-make.pFV-aor-CVB d-ikː-u-li npL-want.IpFV-prog-CVB b.	Передвижение ИГ вложенной клаузы в главную клаузу ʡaˤli-s Ali-dat saj Cop.m šwal five čutːui chudu.aBs rpat’imat-li Patimat-Erg ti d-arq’-ib-lis npL-make.pFV-aor-CVB d-ikː-u-li npL-want.IpFV-prog-CVB (a“b) ‘Али хочет, чтобы Патимат приготовила пять чуду.’',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет) •	Этот вопрос не изучался.',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем одним способом (да / нет) •	Будет уточнено.',
    'В некоторых случаях границы зависимой клаузы могут определяться более чем од- ним способом Да. В случаях, когда матричный и зависимый предикаты приписывают один или тот же падеж, а также когда именная группа немаркирована по падежу (что характерно для имен собственных, личных местоимений, именных групп с посессором и малых имен, имею- щих неопределенную интерпретацию, см. [Аркадьев и др. 2009; Arkadiev, Testelets 2019]), определение принадлежности именной группы матричной или зависимой клаузе может быть затруднено.'
);

INSERT INTO questions (question_number, group_number, question_text, russian, danish, muira, nganasan, polish, circassian) VALUES (
    '1.3',
    '1',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет)',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет) •	Да. Для независимых дативно-инфинитивных предложений типа Вам еще ехать до этой станции анализ зависит от признания их моноклаузальной [Шведова 1980] или биклаузальной структурой [Fleisher 2006; Митренина 2017]. Для таких предложений выделение матричной клаузы над вложенной инфинитивной клаузой возможно при принятии биклаузального анализа и признания нулевого модального предиката, в матричной клаузе Вам MOD [InfP еще ехать до этой станции], см. [Kondrashova 2008]. •	Да. Для глаголов речевой каузации, см. п. 1.2. выше.',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет) •	Нет. Главная клауза всегда содержит финитный глагол и внешне выраженное подлежащее.',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет) Да, см. предыдущие примеры',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет) •	Этот вопрос не изучался.',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом (да / нет) •	Для польского аналитического пассива предлагалось два анализа ― как моноклаузальной структуры и как биклаузальной структуры с подъемом аргумента [Patejuk, Przepiórkowski 2014b].',
    'В некоторых случаях состав главной клаузы может определяться более чем одним способом Да. См. пункт 1.2.'
);

INSERT INTO questions (question_number, group_number, question_text, russian, danish, muira, nganasan, polish, circassian) VALUES (
    '2.1',
    '2',
    'Какой в изучаемом языке базовый порядок слов',
    'Какой в изучаемом языке базовый порядок слов •	SVO, SV [Dryer 2013], со ссылкой на [Bivon 1971], ср. [Циммерлинг 2008; 2016]. Альтернативно ― язык с нефиксированным порядком слов [Ковтунова 1976] или язык с «генерализованной инверсией», т.е. без фиксированного порядка S и O [Bailyn 2004]. Неубедителен анализ русского языка как языка VSO c топикализацией аргументов [King 1995].',
    'Какой в изучаемом языке базовый порядок слов •	V2. V2, XP ―Vfin [Diderichsen 1946; Кузнецов 1984; Vikner 1995; 2020]. •	SVO, SV [Allan et al. 1995; Dryer 2013]. •	Полная схема главной повествовательной клаузы [Кузнецов 1984: 72]: XP ― Vfin ― S ―    NEGGEN/ADVSENT ―   Vinf 	    ―                 OIND ―  ODIR  ― ADV (1)	Så	kunne	han     [sikkert ikke alligevel]   [få                  sagt]	             hende       besked   i tide Так	мочь.INF 3SG.M явно     NEG   однако   получить.INF сказать.PTCP 3SG.F.OBL весть  вовремя ‘Так что он, однако, явно не мог дать ей весть вовремя.’',
    'Какой в изучаемом языке базовый порядок слов Базовый порядок слов – SXOV. Однако от данного стандарта возможны отклонения, обусловлен- ные информационно-структурными причинами (2)	rasul-li	musa-li-s	ʡinc-bi	d-ičː-ib Rasul-Erg Musa-oBL-dat apple.aBs-pL npL-give.pFV-aor ‘Расул дал Мураду яблоки.’',
    'Какой в изучаемом языке базовый порядок слов •	SOV [Wagner-Nagy 2019: 388]',
    'Какой в изучаемом языке базовый порядок слов •	SVO, SV [Brooks 1975: 393; Siewierska 1993: 235; Dryer 2013]. •	W*-система, т. е. язык с внутриклаузальными кластеризуемыми клитиками, использующий множественные стратегии расстановки клитик, в том числе ―  постановку клитик во вторую позицию от начала клаузы (2P), см. [Циммерлинг 2013; Zimmerling, Kosta 2013].',
    'Какой в изучаемом языке базовый порядок слов В качестве базового порядка принято называть SVO [Dryer 2013; Kumakhov, Vamling 2009; Forker 2024]. Впрочем, порядок OSV также весьма частотен [Ландер 2012; Kuma- khov, Vamling 2009; Arkadiev, Lander 2020]. Порядки, при которых субъект или объект находится справа от глагола, засвидетельствованы, но менее частотны [Ландер 2012; Forker 2024]. (2)	hač̣ʼe-m	č̣ʼale-r	ə-λeʁwə-ʁ. guest-OBL	boy-ABS  3SG.ERG-see-PST ‘Гость увидел мальчика.’ [Рогава, Керашева 1966: 65]'
);

