INSERT INTO questions (question_number, group_number, question_text, russian, danish, muira, nganasan, polish, circassian) VALUES (
    '4.4',
    '4',
    'Подъем квантора (да / нет)',
    'Подъем квантора (да / нет) •	Да. [Летучий, Виклова 2020]. По правилу отрицательного согласования, действующему в русском языке, отрицательное кванторное местоимение коррелирует с отрицанием главного предиката (Никто не пришел). В конструкциях вида: Никто ему оказался не нужен отрицание относится не к главному глаголу, а к подчиненному прилагательному, что позволяет предположить подъем из зависимой клаузы: Оказалось, что никто ему не нужен.',
    'Подъем квантора (да / нет) •	будет заполнено',
